import React, { useState } from "react";
import { Card, Typography, Divider, Space, Button } from "antd";
import { StarOutlined, HeartOutlined } from "@ant-design/icons";

const { Title, Text, Paragraph } = Typography;

const Itihas = () => {
  const [isExpanded, setIsExpanded] = useState(false);

  const introText =
    "इतिहास किसी भी समाज के लिए अमूल्य है। इतिहास के बगैर वह राष्ट्र और कौम उन्नति नहीं कर सकता। बीते हुए कल को याद रखने के लिए इतिहास लिखा जाता है। यदि इतिहासकार बीते हुए कल का इतिहास नहीं लिखते तो वर्तमान में जी रही पीढ़ी भूतकाल में हुई घटनाओं से सर्वथा अनभिज्ञ रहती। समय-समय पर इतिहासकारों ने विभिन्न देशों के आदिकाल की सभ्यता पर खोज की और उसे कलमबद्ध कर हम तक पहुँचाया।";

  const fullText = `
  हमें अपने पूर्वजों के श्रेष्ठ कार्यों की जानकारी इतिहास के माध्यम से ही मिल सकती है। जिस जाति के पास अपने पूर्वजों का इतिहास नहीं उसे प्राय: मृत समझा जाता है। अर्थात् जिस व्यक्ति को अपने इतिहास की जानकारी नहीं वो इतिहास का निर्माण नहीं कर सकता। वास्तव में इतिहास ही ज्ञान की कुंजी व ज्ञान का विशाल भंडार होता है। इतिहास वह पवित्र धरोहर है जो जाति को अंधकार से निकाल कर प्रकाश की और ले जाती है। हर व्यक्ति दूसरों से तुलना करके अपने आप को श्रेष्ठ प्रमाणित करने में गौरव महसूस करता है और उसके लिए इतिहास से बढ़कर कोई आधार नहीं हो सकता। किसी जाति को जीवित रखने तथा विकास के पथ पर आगे बढ़ने के लिए इतिहास से अधिक कोई प्रेरणा का स्रोत नहीं हो सकता। इसलिए साहित्य जगत् में इतिहास को भारी महत्व दिया गया है। इतिहास पूर्वजों की अमूल्य निधि है और वही भटके हुए मनुष्यों को मार्ग दिखाता है। भारत वर्ष में विभिन्न धर्मों, सम्प्रदायों, जातियों एवं मत-मतान्तरों के लोग निवास करते हैं। जिसमें हमारा क्षत्रिय खारड़िया सीरवी समाज भी एक है। व्यक्तियों का ऐसा सुपरिभाषित समूह, जो परस्पर सामाजिक सम्बन्धों पर आधारित हो, उसे जाति-समाज कहते हैं। मनुष्य एक सामाजिक प्राणी है। समाज के बिना मनुष्य का कोई पृथक अस्तित्व नहीं रहता। मानव-जाति के अद्भुत और विकास की कहानी अभी तक एक पहेली बनी हुई हैं। एक और जहां विभिन्न धर्मावलम्बी अपने-अपने धर्म एवं दर्शन के अनुसार व्याख्या कर इतिहास लिखते हैं, तो दूसरी और वैज्ञानिक लोग पुरातत्व-विज्ञान का सहारा लेकर अपने अथक प्रयासों से प्रमाण जुटाकर विशुद्ध वैज्ञानिक दृष्टिकोण के अनुरूप मानव-जाति की उत्पत्ति एवं विकास के नए-नए इतिहास का सूत्रपात करते हैं जो भावी पीढ़ी के लिए चिंतन का वर्क पंथ पसंद करता है। आज हर जाति एवं उपजाति की उत्पत्ति का इतिहास भी एक गूढ़ रहस्य बना हुआ है। विभिन्न जातियों की उत्पत्ति के बारे में या तो इतिहास मौन है, या इतिहासकारों ने आपसी विवादों और और तर्क-वितर्को का ऐसा आखाड़ा बना रखा है कि इस प्रकार के विभिन्न मत-मतान्तरों का अध्ययन करने पर भी सच्चाई तक पहुंच पाना दुर्लभ नहीं, कठिन अवश्य है। आज किसी भी जाति की उत्पत्ति के बारे में प्रमाणिकता का दावा नहीं किया जा सकता। फिर भी उपलब्ध प्राचीन इतिहास, साहित्य, शिलालेख, भित्ति चित्र, पौराणिक गाथाओं, राव-भाटों के पास उपलब्ध इतिहास, साहित्य, दंत कथाओं एवं किंवदन्तियों के आधार पर ही किसी जाति की उत्पत्ति एवं विकास के इतिहास तक पहुंचा जा सकता है। उपलब्ध प्राचीन इतिहास का अध्ययन करने से पता चलता है कि सीरवी खारड़िया जाति का उद्भव भी वैदिक क्षत्रिय (राजपूत जाति) से हुआ है, अर्थात हमारे पूर्वज क्षत्रिय राजपूत थे। समाज का मूल स्थान जाबालीपुर के आस-पास और प्राचीन राज्य कोयलपुर (खारी-खाबड़) व फिर जालौर में अपनी आन-बान व शान के साथ क्षत्रिय धर्म का पालन कर समय तत्कालीन परिस्थितियों के अनुरूप 14वीं शताब्दी के पूर्वार्द्ध में तलवार का मोह त्यागकर हल को हाथ में लिया। खारी खाबड़ से आने के कारण खारड़िया और सीर में खेती तथा कृषि कर्म करने से सीरवी कहलाए। इस प्रकार सदैव सिरमोर रहने वाला सीरवी (क्षत्रिय) खारड़िया समाज आज मूलत: मारवाड़, मेवाड़, मालवा, निमाड़ में तथा व्यवसायिक दृष्टि में सम्पूर्ण भारतवर्ष में तथा विदेशों में आवासित है। हमारा सीरवी समाज सदियों से कृषि और पशुपालन से आजीविका कमाने वाला रहा है। अशिक्षा के सघन अन्धकार में जीवन-जीने वाला सीरवी समाज में राव-भाटों की प्राचीन बहियों में कैद इतिहास को सुनकर तथा कुछ इतिहासकारों द्वारा लिखी इतिहास पुस्तकों का गहन अध्ययन करके समाज के प्रथम इतिहासकार स्वर्गीय श्री शिवसिंह मल्लाजी चोयल (भावी) ने अपनी लेखनी से सीरवी समाज का इतिहास लिखकर अपनी विशिष्ट पहचान के लिए जूझते समाज में आशा की एक किरण जगाई और समाज के अज्ञात इतिहास को लोक कथाओं, किंवदन्तियों और जनश्रुतियों को अपने जाति-समाज, कुल-गोत्र, पूर्वजों और बीते अतीत जानकारी मिलती हैं। इतिहास मनुष्य को अपने जातीय संस्कारों के साथ सभ्यता और संस्कृति से सदैव जुड़े रहने का संदेश देता रहता है। इतिहास से हमें अपने पूर्वजों के जीवन लक्ष्य की जानकारी मिलती है। जिसमें उनके उत्तराधिकारी हम अपने दायित्व का उचित निर्वहन कर सकें। मनुष्य समाज द्वारा बनाई गई प्रथाओं, परम्पराओं, रीति-रिवाजों, परिपाटियों और मान्य मर्यादाओं का पालन करते हुए अपना जीवन-यापन करता है। समाज की मर्यादाओं का तात्पर्य – व्यवहारिक नियमों से है, जो लोक रीतियों, परम्पराओं, प्रथाओं, लोकाचारों, रीति-रिवाजों और धार्मिक मान्यताओं पर आधारित होता है। जिन्हें हम समाज का संविधान भी कर सकते हैं इसमें समाज की व्यवस्था का संचालन होता हैं।`;

  const toggleExpand = () => {
    setIsExpanded(!isExpanded);
  };

  return (
    <div
      style={{
        minHeight: "100vh",
        background: "#f0f2f5",
        padding: "1px",
        fontFamily: 'Inter, "Noto Serif Devanagari", sans-serif',
      }}
    >
      <div style={{ maxWidth: "1200px", margin: "0 auto" }}>
       
          <div style={{ textAlign: "center", padding: "16px" }}>
            <Space direction="vertical" size="middle">
              <div
                style={{
                  display: "flex",
                  justifyContent: "center",
                  alignItems: "center",
                  gap: "12px",
                }}
              >
                <StarOutlined style={{ fontSize: "24px", color: "#ff6b6b" }} />
                <Title
                  level={2}
                  style={{
                    margin: 0,
                    color: "#1f2937",
                    fontSize: "24px",
                    fontWeight: "600",
                  }}
                >
                  समाज का इतिहास
                </Title>
                <StarOutlined style={{ fontSize: "24px", color: "#ff6b6b" }} />
              </div>
              <Text style={{ color: "#6b7280", fontSize: "16px" }}>
                सीरवी खारड़िया क्षत्रिय समाज
              </Text>
              <div
                style={{
                  display: "flex",
                  justifyContent: "center",
                  gap: "6px",
                }}
              >
                {[...Array(5)].map((_, i) => (
                  <StarOutlined
                    key={i}
                    style={{ color: "#f59e0b", fontSize: "16px" }}
                  />
                ))}
              </div>
            </Space>
          </div>
          <Divider style={{ borderColor: "#e5e7eb", margin: "16px 0" }} />
          <div style={{ padding: "0 16px" }}>
            <Card
              size="small"
              style={{
                borderRadius: "8px",
                border: "1px solid #e5e7eb",
                boxShadow: "0 1px 4px rgba(0,0,0,0.05)",
                background: "#ffffff",
                marginBottom: "16px",
              }}
            >
              <Paragraph
                style={{
                  fontSize: "16px",
                  lineHeight: "1.8",
                  textAlign: "justify",
                  color: "#1f2937",
                }}
              >
                {introText}
              </Paragraph>
            </Card>
            {isExpanded && (
              <Card
                size="small"
                style={{
                  borderRadius: "8px",
                  border: "1px solid #e5e7eb",
                  boxShadow: "0 1px 4px rgba(0,0,0,0.05)",
                  background: "#ffffff",
                  marginBottom: "16px",
                }}
              >
                <Paragraph
                  style={{
                    fontSize: "16px",
                    lineHeight: "1.8",
                    textAlign: "justify",
                    color: "#1f2937",
                  }}
                >
                  {fullText}
                </Paragraph>
              </Card>
            )}
            <div style={{ textAlign: "center", marginTop: "16px" }}>
              <Button
                type="primary"
                onClick={toggleExpand}
                style={{
                  background: "#ff6b6b",
                  borderColor: "#ff6b6b",
                  borderRadius: "6px",
                  padding: "8px 16px",
                }}
              >
                {isExpanded ? "कम पढ़ें" : "पूरा इतिहास पढ़ें"}
              </Button>
            </div>
          </div>
          <Divider style={{ borderColor: "#e5e7eb", margin: "16px 0" }} />
          <div style={{ textAlign: "center", padding: "16px" }}>
            <Space>
              <HeartOutlined style={{ color: "#ff6b6b", fontSize: "16px" }} />
              <Text style={{ color: "#6b7280", fontSize: "14px" }}>
                श्री सीरवी खारड़िया समाज की जय हो
              </Text>
              <HeartOutlined style={{ color: "#ff6b6b", fontSize: "16px" }} />
            </Space>
          </div>
       
      </div>
    </div>
  );
};

export default Itihas;
